<?php/** * */add_theme_support('custom-logo', array(    'height' => 50,    'width' => 200,    'flex-height' => true,    'flex-width' => true,    'header-text' => array('site-title', 'site-description'),));/** * @return mixed */function fafThemeLogo(){    $imgId          = get_theme_mod('custom_logo');    $data['src']    = wp_get_attachment_image_url($imgId, 'thumbnail');    $data['set']    = wp_get_attachment_image_srcset($imgId, 'thumbnail');    $data['sizes']  = wp_get_attachment_image_sizes($imgId, 'thumbnail');    return $data;}